const { getFiles } = require('./fn');


module.exports = (globalVariable) => {
    const servicePaths = getFiles(globalVariable.rootDir, 'services', 'service.js');
    const services = servicePaths.reduce((t, c) => {
        const s = require(globalVariable.rootDir + '/' + c.path);
        t[c.name] = s;
        return t;
    }, {});
    return services;
}