const { getFiles } = require('./fn');

module.exports = (globalVariable) => {
    const mPaths = getFiles(globalVariable.rootDir, 'types', 'type.js');
    const types = mPaths.reduce((t, c) => {
        const s = require(globalVariable.rootDir + '/' + c.path);
        t[c.name] = s;
        return t;
    }, {});
    return types;
}